    <>

      {[false].map((expand) => (
    <Carousel fade key={expand} bg="light" expand={expand} >
        {items.map(item => (     
             <Carousel.Item>
             <img  className="d-block w-100" src={`https://indianculture.nvli.in${item.field_background_slider_image}`}  alt={item.title}   width="100px" height="400px"      />
             <Carousel.Caption>
               <h3>{item.title}</h3> 
             </Carousel.Caption>
           </Carousel.Item>
              ))}   
    </Carousel>
   ))}
   </>

 );
